import React from 'react'
import { Link } from 'react-router-dom'
import { useState } from 'react'

export default function Login() {
  const [cred, setcred] = useState({name:"",rollno:"",phno:"",email:"",password:""})

  const handleSubmit=async(e)=>{
    e.preventDefault();
    const response=await fetch("http://localhost:5000/create",{
    method :'POST',
    headers :{
       'Content-Type' :'application/json'
    },
    body:JSON.stringify({email:cred.email,password:cred.password})
});
const json=await response.json();
console.log(json)
if(json.success){
  
  alert("Success")
}
else{
  alert("Enter valid credentials")
}
}
const onChange=(e)=>{
  setcred({...cred,[e.target.name]: e.target.value})
}
  return (
    <div>
        <div className="container">
        <form onSubmit={handleSubmit} >

  <div className="mb-3">
    <label htmlFor="exampleInputEmail1" className="form-label">Email address</label>
    <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" name='email'value={cred.email} onChange={onChange}/>
    <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
  </div>
  <div className="mb-3">
    <label htmlFor="exampleInputPassword1" className="form-label">Password</label>
    <input type="password" className="form-control" id="exampleInputPassword1" name='password' value={cred.password} onChange={onChange}/>
  </div>
  <button type="submit" className="btn btn-success m-3">Submit</button>
 <Link to='/signup'>  <button type="submit" className="btn btn-danger">Register</button></Link>
</form>
    </div>
    </div>
  )
}
