import React from 'react'
import {useState,useEffect} from 'react'
import Card from '../components/Card'
import Carosel from '../components/Carosel'
import Footer from '../components/Footer'
import Navbar from '../components/Navbar'

export default function Home() {
const [search, setsearch] = useState("")
const [food, setfood] = useState([]);
const [food_cat, setfood_cat] = useState([]);

const load=async()=>{
  let response=await fetch("http://localhost:5000/getdata",{
    method:"POST",
    headers:{
      'Content-Type':'application/json'
    }
  });
  response=await response.json();
  // console.log(response[0]);
  setfood(response[0]);
  setfood_cat(response[1]);
}
useEffect(() => {
  load();
 
}, [])


  return (
    <div>
    <div><Navbar/> </div>
    
    <div
        id="carouselExampleFade"
        className="carousel slide carousel-fade"
        data-bs-ride="carousel"
        style={{objectFit:"contain !important" }}
      >
     
        <div className="carousel-inner" style={{ height: "600px"  }}>
        
          <div className="carousel-item active">
            <img
              src="https://source.unsplash.com/1600x900/?noodles"
              className="d-block w-100"
              alt="..."
            />
          </div>
          <div className="carousel-item">
            <img
              src="https://source.unsplash.com/1600x900/?momo"
              className="d-block w-100"
              alt="..."
            />
          </div>
          <div className="carousel-item">
            <img
              src="https://source.unsplash.com/1600x900/?burger"
              className="d-block w-100"
              alt="..."
            />
          </div>
        </div>
        <div className=" carousel-caption" >
          <div className="carousel-item active">
            <div className="d-flex justify-content-center">
              <input
                type="search"
                className="form-control me-2 rounded"
                placeholder="Search"
                aria-label="Search"
                aria-describedby="search-addon"
                value={search}
                onChange={(e)=>{setsearch(e.target.value)}}
              />
              <button type="button" className="btn btn-outline-danger btn-danger " style={{color:"white"}}>
                search
              </button>
            </div>
          </div>
        </div>
        <button
          className="carousel-control-prev"
          type="button"
          data-bs-target="#carouselExampleFade"
          data-bs-slide="prev"
        >
          <span
            className="carousel-control-prev-icon"
            aria-hidden="true"
          ></span>
          <span className="visually-hidden">Previous</span>
        </button>
        <button
          className="carousel-control-next"
          type="button"
          data-bs-target="#carouselExampleFade"
          data-bs-slide="next"
        >
          <span
            className="carousel-control-next-icon"
            aria-hidden="true"
          ></span>
          <span className="visually-hidden">Next</span>
        </button>
      </div>


    <div className='container'>
  {
    food_cat!==[]
    ?food_cat.map((data)=>{
      return(
        <div className='row mb-3'>
        <div key={data._id} className="fs-3 m-3">
            {data.CategoryName}
        </div>
        <hr/>
        {food!==[]? food.filter((foods)=>foods.CategoryName===data.CategoryName)
        .map(filterItems=>{
          return(
            <div key={filterItems.id} className='col-12 col-md-6 col-lg-3 '>
                  <Card foodName={filterItems.name}
                  options={filterItems.options[0]}
                  imgsrc={filterItems.img}
                  ></Card>
            </div>
          )
        }):<div> No Data </div>}
        </div>
      )
    }):<div>"""" </div>
  }
   
    </div>
    <div><Footer/> </div>
    </div>
  )
}
