import React from 'react'
import { Link } from 'react-router-dom'
import { useState } from 'react'
export default function Signup() {

const [cred, setcred] = useState({name:"",rollno:"",phno:"",email:"",password:""})

    const handleSubmit=async(e)=>{
        e.preventDefault();
        const response=await fetch("http://localhost:5000/create",{
        method :'POST',
        headers :{
           'Content-Type' :'application/json'
        },
        body:JSON.stringify({name:cred.name,rollNo:cred.rollno,phno:cred.phno,email:cred.email,password:cred.password})
    });
    const json=await response.json();
    console.log(json)
    if(!json.success){
      alert("Enter valid credentials")
    }
    else{
      alert("")
    }
    }
    const onChange=(e)=>{
      setcred({...cred,[e.target.name]: e.target.value})
    }
  return (
    <div>
        <div className="container">
        <form onSubmit={handleSubmit} >
        <div className="mb-3">
    <label htmlFor="Name" className="form-label">Name</label>
    <input type="text" className="form-control" name='name' value={cred.name} onChange={onChange}  />

  </div>
  <div className="mb-3">
    <label htmlFor="rollno" className="form-label">Roll No</label>
    <input type="text" className="form-control" name='rollno' value={cred.rollno} onChange={onChange} />

  </div>
  <div className="mb-3">
    <label htmlFor="Phone Number" className="form-label">Phone Number</label>
    <input type="text" className="form-control" name='phno' value={cred.phno} onChange={onChange} />

  </div>

  <div className="mb-3">
    <label htmlFor="exampleInputEmail1" className="form-label">Email address</label>
    <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" name='email'value={cred.email} onChange={onChange}/>
    <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
  </div>
  <div className="mb-3">
    <label htmlFor="exampleInputPassword1" className="form-label">Password</label>
    <input type="password" className="form-control" id="exampleInputPassword1" name='password' value={cred.password} onChange={onChange}/>
  </div>
  <button type="submit" className="btn btn-success m-3">Submit</button>
 <Link to='/login'>  <button type="submit" className="btn btn-danger">Already a member</button></Link>
</form>
    </div>
    </div>
  )
}
