import React from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { useState } from 'react'

export default function Login() {
  const [cred, setcred] = useState({email:"",password:""})
  navigate=useNavigate()

  const handleSubmit=async(e)=>{
    e.preventDefault();
    const response=await fetch("http://localhost:5000/login",{
    method :'POST',
    headers :{
       'Content-Type' :'application/json'
    },
    body:JSON.stringify({email:cred.email,password:cred.password})
});
const json=await response.json();
console.log(json)
if(json.success){
  navigate("/")
}
}
const onChange=(e)=>{
  setcred({...cred,[e.target.name]: e.target.value})
}
  return (
    <div>
        <div className="container">
        <form onSubmit={handleSubmit} >

  <div className="mb-3">
    <label htmlFor="exampleInputEmail1" className="form-label">Email address</label>
    <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" name='email'value={cred.email} onChange={onChange}/>
    <div id="emailHelp" className="form-text"></div>
  </div>
  <div className="mb-3">
    <label htmlFor="exampleInputPassword1" className="form-label">Password</label>
    <input type="password" className="form-control" id="exampleInputPassword1" name='password' value={cred.password} onChange={onChange}/>
  </div>
  <button type="submit" className="btn btn-success m-3">Login</button>
 <Link to='/signup'>  <button type="submit" className="btn btn-danger">Register</button></Link>
</form>
    </div>
    </div>
  )
}
